mapper.select:

select no, date, expenses, "desc" as desc, user_no, orderstate_no, patient_no
from "order"
where patient_no = #{patientNo }

mapper.update:

UPDATE "order"
SET date = to_timestamp(#{date}, 'YYYY-MM-DD HH24:MI:SS')
WHERE no = #{no }


UPDATE "order"
SET orderstate_no = #{orderstateNo },
	expenses = #{expenses}
WHERE no = #{no }
