mapper.select:

select no, name, gender, rrn, insuarance, phone, address, length, weight, 'desc' as desc
from patient 
where no = #{patientNo }

mapper.update:

UPDATE patient
SET name=#{name}, rrn=#{rrn}, insuarance=#{insuarance}, phone=#{phone}, address=#{address}, length=#{length}, weight=#{weight}, "desc"=#{desc}
WHERE no = #{no}
